var tcs = new $.Deferred();
context.post( ko.mapping.toJSON(entity),'/patient/validate/${1:business rules}')
.done(function(result){
    if (result.success) {
        logger.info(result.message);
        errors.removeAll();
        app.showMessage("${2: valid message}", "${3:title}", ["OK"]);;

    } else {
        errors.removeAll();
        _(result.rules).each(function (v) {
            errors(v.ValidationErrors);
        });
        logger.error("${4:invalid message}");
    }
    tcs.resolve(true);
});

return tcs.promise();
